const Main = (function() {
	function Main() {}
	
	Main.prototype = {
		render: function() {
			//get info for forms
			fetch(`/api/artists`)
			.then(res => res.json())
			.then(data => {
				
				const select = document.createElement(select);
				select.name = "artist"
				
				data.forEach(artist => {
					select.innerHTML += `
						<option value="${artist.id}">${artist.name}</option>
					`
				})
			})
			
			//return page
			return `
				<main>
					<article class="addArtist">
						<label>Artist Name: <input type="text" name="name" /></label>
						<label>Rating: <input type="number" name="rating" min="0" max="10"></label>
						<label>Artist Name: <input type="text" name="imageUrl" /></label>
						<button>Add Artist</button>
					</article>
					
					<article class="addAlbum">
						<label>Album Title: <input type="text" name="title" /></label>
						<label>Rating: <input type="number" name="rating" min="0" max="10"></label>
						<label>Record Label: <input type="text" name="recordLabel"></label>
						<label>Artist: 
							${this.select}
						</label>
						<button>Add Album</button>
					</article>
					
					<article class="addSong">
						<label>Song Title: <input type="text" name="title" /></label>
						<label>Rating: <input type="number" name="rating" min="0" max="10"></label>
						<label>Duration: <input type="text" name="duration"></label>
						<label>Album: 
							<select name="album">
							  <option value="volvo">Volvo</option>
							</select>
						</label>
						<button>Add Song</button>
					</article>
					
				</main>
			`
		}
	}

	return Main
})()

module.exports = {
	Main
}

