const ArtistPage = (function() {
	function ArtistPage(id) {
		this.id = id
	}
	
	ArtistPage.prototype = {
		render: function() {
			fetch(`localhost:8080/api/artists/${this.id}`)
			.then(res => res.json())
			.then(data => {
					return `
					<article>
						<h2>${data.name}</h2>
					</article>
					`
				})
		}
	}

	return ArtistPage
})()

module.exports = {
	ArtistPage
}

